// SetSN of the post to the related item.
// Sets o.R.SN to related.
// Adds o to related.R.Posts.
func (o *Post) SetSN(ctx context.Context, exec boil.ContextExecutor, insert bool, related *SN) error {
	var err error
	if insert {
		if err = related.Insert(ctx, exec, boil.Infer()); err != nil {
			return errors.Wrap(err, "failed to insert into foreign table")
		}
	}

	updateQuery := fmt.Sprintf(
		"UPDATE \"post\" SET %s WHERE %s",
		strmangle.SetParamNames("\"", "\"", 1, []string{"sns_id"}),
		strmangle.WhereClause("\"", "\"", 2, postPrimaryKeyColumns),
	)
	values := []interface{}{related.ID, o.ID}

	if boil.IsDebug(ctx) {
		writer := boil.DebugWriterFrom(ctx)
		fmt.Fprintln(writer, updateQuery)
		fmt.Fprintln(writer, values)
	}
	if _, err = exec.ExecContext(ctx, updateQuery, values...); err != nil {
		return errors.Wrap(err, "failed to update local table")
	}

	o.SNSID = related.ID
	if o.R == nil {
		o.R = &postR{
			SN: related,
		}
	} else {
		o.R.SN = related
	}

	if related.R == nil {
		related.R = &snR{
			Posts: PostSlice{o},
		}
	} else {
		related.R.Posts = append(related.R.Posts, o)
	}

	return nil
}

